def PrintWall(ImportedList):

    pos = mc.player.getTilePos()
    mc.player.setPos(pos.x,pos.y,pos.z)

    myList = ImportedList
    

    for row in range (0,30):
        for column in range (0,30):
            
            mc.setBlock(pos.x+column,pos.y+row,pos.z-20,myList[row][column])    
            

